export default {
  // Exclude .d.ts files (auto-generated by Next.js, TypeScript, etc.)
  '*.{js,jsx,ts,tsx}': files => {
    const filtered = files.filter(f => !f.endsWith('.d.ts'));
    if (filtered.length === 0) return [];
    return [`eslint --fix ${filtered.join(' ')}`, `prettier --write ${filtered.join(' ')}`];
  },
  '*.{json,yml,yaml}': ['prettier --write'],
  '*.{md,mdx}': ['markdownlint-cli2 --fix', 'prettier --write'],
};
